import UserCard from "./Component/UserCard/UserCard";
import Stats from "./Component/Stats/Stats";

function App() {
  const UserStat = {
    goodName: "Jeremy Robson",
    stats: ["Daily", "Weekly", "Monthly"],
    content: "Reports For",
  };

  const stat = [
    {
      title: "Work",
      timeframes: [
        {
          name: "daily",
          current: 5,
          previous: 7,
        },
        {
          name: "weekly",
          current: 32,
          previous: 36,
        },
        {
          name: "monthly",
          current: 103,
          previous: 128,
        },
      ],
    },
    {
      title: "Play",
      timeframes: [
        {
          name: "daily",
          current: 1,
          previous: 2,
        },
        {
          name: "weekly",
          current: 10,
          previous: 8,
        },
        {
          name: "monthly",
          current: 23,
          previous: 29,
        },
      ],
    },
    {
      title: "Study",
      timeframes: [
        {
          name: "daily",
          current: 0,
          previous: 1,
        },
        {
          name: "weekly",
          current: 4,
          previous: 7,
        },
        {
          name: "monthly",
          current: 13,
          previous: 19,
        },
      ],
    },
    {
      title: "Exercise",
      timeframes: [
        {
          name: "daily",
          current: 1,
          previous: 1,
        },
        {
          name: "weekly",
          current: 4,
          previous: 5,
        },
        {
          name: "monthly",
          current: 11,
          previous: 18,
        },
      ],
    },
    {
      title: "Social",
      timeframes: [
        {
          name: "daily",
          current: 1,
          previous: 3,
        },
        {
          name: "weekly",
          current: 5,
          previous: 10,
        },
        {
          name: "monthly",
          current: 21,
          previous: 23,
        },
      ],
    },
    {
      title: "Self Care",
      timeframes: [
        {
          name: "daily",
          current: 0,
          previous: 1,
        },
        {
          name: "weekly",
          current: 2,
          previous: 2,
        },
        {
          name: "monthly",
          current: 7,
          previous: 11,
        },
      ],
    },
  ];
  function handleStatChange(stats) {
    console.log(stats.toLowerCase());
    const filteredWork = stat
      .map((val) => {
        return val.timeframes.filter((val) => {
          return val.name === stats.toLowerCase();
        });
      })
      .flat();

    console.log(filteredWork);
  }

  return (
    <div className="container">
      <div className="row">
        <UserCard onStatChange={handleStatChange} data={UserStat} />

        <Stats />
      </div>
    </div>
  );
}

export default App;
